sp_widget.client_script:
function() {
	/* widget controller */
	var c = this;
	//c.data.notes = [];
	//c.server.update().then(function(resp){alert(c.data.notes);})
	c.selectItem = function(idx) {
		var id = c.data.notes[idx].sys_id;
		console.log('Note ID: ' + id);
		$rootScope.noteID = id;
		$rootScope.$emit('selectNote', id);
	}

}

sp_widget.script:
(function() {
	/* populate the 'data' object */
	/* e.g., data.table = $sp.getValue('table'); */
	data.notes = [];


	var ngr = new GlideRecord('x_snc_createnotes_note');
	ngr.addQuery('user',gs.getUserID());
	ngr.orderByDesc('sys_created_on');
	ngr.query();
	while(ngr.next())
	{
		var noteObj = {};
		$sp.getRecordDisplayValues(noteObj, ngr, 'number,title,sys_id');
		noteObj.note = ngr.getValue('note').slice(0,20);
		data.notes.push(noteObj);
	}


})();
